import http.client
import time
import urllib

import requests
from pushover import Pushover

# import smtplib
# from email.mime.text import MIMEText
# from email.mime.multipart import MIMEMultipart
# from email.mime.application import MIMEApplication

# List of locations to check
locations = ["rh2 7lu", "ng9 3ap", "rg14 7gj", "pe6 9nr", "LA9 4DZ"]

# Apple URL for checking stock availability
url_template = "https://www.apple.com/uk/shop/fulfillment-messages?pl=true&mts.0=regular&parts.0=MU793ZD/A&location={}"

# Function to check iPhone stock availability at a specific location
def check_stock(location):
    response = requests.get(url_template.format(location))
    data = response.json()

    # Check if any of the 12 stores have stock available
    for store in data["body"]["content"]["pickupMessage"]["stores"]:
        if store["partsAvailability"]["MU793ZD/A"]["pickupDisplay"] == "unavailable":
            return True  # iPhones are in stock
    return False  # iPhones are still out of stock


# Function to send Pushover notification
def send_pushover_notification(message):
    po = Pushover("af113qxrsrtcj8su31mefve19jjhwx")
    po.user("ubfv1ka9qz55cqzp5v6z28nie5j19r")

    msg = po.msg(message)

    msg.set("title", "iPhone is available!!!")

    po.send(msg)
    print("sent message to phone")


# Main loop to periodically check for stock at each location
while True:
    for location in locations:
        if check_stock(location):
            message = f"iPhones are in stock at location: {location}!"
            send_pushover_notification(message)
    print("Waiting for an hour before checking again...")
    # time.sleep(3600)
